floor = 0

for index, char in enumerate(data):
    if char == '(':
        floor = floor + 1
    if char == ')':
        floor = floor - 1

    if floor == -1:
        floorIntoBasement = index + 1
        break

print("First time in basement: " + str(floorIntoBasement))
print("Floor value, for confirmation: " + str(floor))
